import styled from 'styled-components';

export const ModalBackground = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    position: fixed;
`;

export const Modaltest = styled.div`
    position: fixed;
    background: rgba(0, 0, 0, 0.7);
    width: 100%;
    height: 25%;
`;

export const ModalContainer = styled.div`
    position: fixed;
    background: #fff;
    width: 60%;
    max-width: 600px;
    height: 600px;
    overflow-y: auto;
    margin: 0 auto;
    padding: 10px 10px;
    border-radius: 5px;
    background: #fff;
    border-radius: 30px;
    z-index: 2;

    ul {
        list-style: none;
    }

    img{
        width: 500px;
    }
`;

export const ModalTitle = styled.h2`
    font-size: 24px;
    margin-bottom: 10px;
`;

export const ModalContent = styled.p`
    font-size: 16px;
`;

export const ModalClose = styled.div`
    display: flex;
    align-items: center;
    margin-left: 20px;
    margin-top: 10px;
    background-color: black;
    width: 50px;
    color: white;
`;
