import React, { useEffect } from 'react';
import { ModalBackground, ModalContainer, ModalContent, ModalTitle, Modaltest } from '../StyleComponents/ModalStyled';
import mypage from "../img/mypage.gif";
import pay from "../img/pay.gif";
function ModalPage({ isModalOpen, setIsModalOpen }) {
    const handleClose = (e) => {
        e.stopPropagation();
        setIsModalOpen(false);
    }

    useEffect(() => {
        if (isModalOpen) {
            // 모달이 열릴 때 스크롤을 막음
            document.body.style.overflow = 'hidden';

            // 추가: 모달이 열릴 때 요소를 선택하여 스크롤을 막음
            const element = document.getElementById('scrollableElement');
            if (element) {
                element.addEventListener('scroll', preventScroll);
                element.addEventListener('touchmove', preventScroll);
                element.addEventListener('mousewheel', preventScroll);
            }
        } else {
            // 모달이 닫힐 때 스크롤을 허용
            document.body.style.overflow = 'auto';

            // 추가: 모달이 닫힐 때 이전에 선택한 요소에서 이벤트 리스너 제거
            const element = document.getElementById('scrollableElement');
            if (element) {
                element.removeEventListener('scroll', preventScroll);
                element.removeEventListener('touchmove', preventScroll);
                element.removeEventListener('mousewheel', preventScroll);
            }
        }
    }, [isModalOpen]);

    // 추가: 스크롤을 막기 위한 이벤트 핸들러 함수
    const preventScroll = (e) => {
        e.preventDefault();
        e.stopPropagation();
        return false;
    }

    return (
        <ModalBackground >
            <Modaltest onClick={handleClose} />
            <ModalContainer>
                <ModalTitle>🏰 첫번째 프로젝트 DreamStay</ModalTitle>
                <ModalContent>
                    기능구현목록
                    <ul>
                        <li>
                            <h2>회원가입</h2>
                        </li>

                        <li>
                            <h2>마이페이지</h2>
                        </li>
                        <img src={mypage} alt='mypage' className='mypagepng' />
                        <li>
                            <h2>예약페이지</h2>
                        </li>
                        <img src={pay} alt='pay' className='paypng' />
                        <li>
                            <h2>예약완료페이지</h2>
                        </li>
                        <li>
                            <h2>비회원 예약확인 페이지</h2>
                        </li>
                        <li>
                            <h2>Q&A css</h2>
                        </li>
                    </ul>
                </ModalContent>
            </ModalContainer>
        </ModalBackground>
    );
}

export default ModalPage;
