import React, { useEffect, useState } from 'react';
import { SectionsContainer, Section } from 'react-fullpage';
import "../Style/LayOut.css";
import { Container1, Container1Inner, Container1Link, Container2 } from '../StyleComponents/StyleComponent';
import profile from "../img/profile.png";
import { Link } from 'react-router-dom';
function Layout(props) {

    let options = {
        anchors: ['sectionOne', 'sectionTwo', 'sectionThree'],
        navigation: false,
        // delay: 1000,
    };

    const [colorIndex, setColorIndex] = useState(0);
    const colors = ['blue', 'red', 'green', 'purple'];

    useEffect(() => {
        const text = document.querySelector('.InfoSpan1');
        if (text) {
            const letters = text.textContent.split('');
            text.innerHTML = '';

            letters.forEach((letter, index) => {
                const span = document.createElement('span');
                span.textContent = letter;
                span.style.transition = 'color 1s';
                text.appendChild(span);
            });

            const interval = setInterval(() => {
                letters.forEach((letter, index) => {
                    const span = text.children[index];
                    setTimeout(() => {
                        span.style.color = colors[colorIndex];
                    }, 100 * index);
                });

                setColorIndex((colorIndex + 1) % colors.length);
            }, 2000); // Change the interval (in milliseconds) to control the speed

            return () => {
                clearInterval(interval);
            };
        }
    }, [colorIndex]);

    return (
        <SectionsContainer {...options}>
            <Section>
                <Container1>
                    <Container1Inner>
                        <span className='InfoSpan1'>Lee Sang Hyuk</span>
                        <span className='InfoSpan2'>FRONT-END DEVLOPER</span>
                        <Container1Link>
                        <a href='https://github.com/helloa1109' target='_blank' rel='noopener noreferrer' className='InfoLink'>GitHub</a>
                        <a href='https://www.notion.so/my-name-is-Sang-Hyuk-5995b4ab5c0448539c698da2bd0bd612?pvs=4' target='_blank' rel='noopener noreferrer' className='InfoLink'>Notion</a>
                        </Container1Link>
                    </Container1Inner>
                </Container1>
            </Section>

            <Section>
                <Container2>
                    <span>섹션2</span>
                </Container2>
            </Section>

            <Section>
                Page 3
            </Section>
        </SectionsContainer>
    );
}

export default Layout;
