import React, { useEffect, useRef } from 'react';
import { ModalBackground, ModalContainer, ModalContent, ModalTitle, Modaltest } from '../StyleComponents/ModalStyled';
import mypage from "../img/mypage.gif";
import pay from "../img/pay.gif";
function ModalPage({ isModalOpen, setIsModalOpen }) {
    const handleClose = (e) => {
        e.stopPropagation();
        setIsModalOpen(false);
    }

    const modalRef = useRef(null);

    const preventScroll = (e) => {
        if (modalRef.current && !modalRef.current.contains(e.target)) {
            e.preventDefault();
        }
    }

    useEffect(() => {
        if (isModalOpen) {
            // 모달이 열릴 때 스크롤을 막음
            document.body.style.overflow = 'hidden';
            window.addEventListener('mousewheel', preventScroll, { passive: false });
        } else {
            // 모달이 닫힐 때 스크롤을 허용
            document.body.style.overflow = 'auto';
            window.removeEventListener('mousewheel', preventScroll);
        }
    }, [isModalOpen]);

    return (
        <ModalBackground >
            <Modaltest onClick={handleClose} />
            <ModalContainer>
                <ModalTitle>🏰 첫번째 프로젝트 DreamStay</ModalTitle>
                <ModalContent>
                    기능구현목록
                    <ul>
                        <li>
                            <h2>회원가입</h2>
                        </li>

                        <li>
                            <h2>마이페이지</h2>
                        </li>
                        <img src={mypage} alt='mypage' className='mypagepng' />
                        <li>
                            <h2>예약페이지</h2>
                        </li>
                        <img src={pay} alt='pay' className='paypng' />
                        <li>
                            <h2>예약완료페이지</h2>
                        </li>
                        <li>
                            <h2>비회원 예약확인 페이지</h2>
                        </li>
                        <li>
                            <h2>Q&A css</h2>
                        </li>
                    </ul>
                </ModalContent>
            </ModalContainer>
        </ModalBackground>
    );
}

export default ModalPage;
