import React, { useEffect } from 'react';
import { HeaderContainer, HeaderInner, HeaderMenu } from '../StyleComponents/StyleComponent';
import "../Style/Header.css";
import { useLocation, useNavigate } from 'react-router-dom';
function Header(props) {

    const { activeSection, setActiveSection } = props;

    const handleSectionChange = (sectionId) => {
        setActiveSection(sectionId);
    };

    useEffect(() => {
        const sectionElements = document.querySelectorAll('.Section'); 
        sectionElements.forEach((section) => {
            if (activeSection) {
                section.classList.add('active-section');
            } else {
                section.classList.remove('active-section'); 
            }
        });
    }, [activeSection]);

    const location = useLocation();
    const navigate = useNavigate();

    // 메뉴 클릭 시 해당 경로로 이동
    const handleMenuClick = (path) => {
        navigate(path);
    };

    // 사용자가 수동으로 URL을 변경하거나 페이지가 로드될 때 섹션을 설정
    useEffect(() => {
        const sections = ['sectionOne', 'sectionTwo', 'sectionThree', 'sectionFour'];
        const currentSection = sections.find((section) => location.hash === `#${section}`);
        if (currentSection) {
            // 이 부분에서 react-fullpage와 함께 스크롤 처리를 수행할 수 있습니다.
            // currentSection 값을 사용하여 해당 섹션으로 스크롤합니다.
        }
    }, [location]);


    return (
        <HeaderContainer>
            <HeaderInner>
                <h1>이상혁</h1>
                <HeaderMenu>
                <nav>
                <ul>
                    <li onClick={() => handleMenuClick('/#sectionOne')}>Home</li>
                    <li onClick={() => handleMenuClick('/#sectionTwo')}>About</li>
                    <li onClick={() => handleMenuClick('/#sectionThree')}>Skills</li>
                    <li onClick={() => handleMenuClick('/#sectionFour')}>Projects</li>
                </ul>
            </nav>

                </HeaderMenu>
            </HeaderInner>
        </HeaderContainer>
    );
}

export default Header;
