import styled from 'styled-components';

export const ModalBackground = styled.div`
    top: 75%;
    width: 100vw;
    height: 100vh;

    z-index: 1;
    display: flex;
    align-items: center;
`;

export const Modaltest = styled.div`
    position: fixed;
    background: rgba(0, 0, 0, 0.7);
    width: 100%;
    height: 100%;
`;

export const ModalContainer = styled.div`
    background: #fff;
    width: 60%;
    max-width: 600px;
    height: 700px;
    overflow-y: auto;
    margin: 0 auto;
    padding: 10px 10px;
    border-radius: 5px;
    background: #fff;
    border-radius: 5px;
    z-index: 9;

    ul {
        list-style: none;
    }

    img{
        width: 500px;
    }
`;

export const ModalTitle = styled.h2`
    font-size: 24px;
    margin-bottom: 10px;
`;

export const ModalContent = styled.p`
    font-size: 16px;
`;
