import React, { useEffect, useRef } from 'react';
import '../App.css';

function Layout(props) {
  const wrapRef = useRef();
  let page = 0;
  const totalContainers = 4; // Total number of containers
  const containerHeight = window.innerHeight; // Height of each container

  useEffect(() => {
    const wheelHandler = (e) => {
      e.preventDefault();
      const deltaY = e.deltaY;

      if (deltaY > 0 && page < totalContainers - 1) {
        page++;
      } else if (deltaY < 0 && page > 0) {
        page--;
      }

      wrapRef.current.style.transform = `translateY(-${page * containerHeight}px)`;
    };

    wrapRef.current.addEventListener('wheel', wheelHandler);

    return () => {
      wrapRef.current.removeEventListener('wheel', wheelHandler);
    };
  }, []);

  return (
    <div className='full-page-scroll' ref={wrapRef} style={{ height: totalContainers * 100 + 'vh' }}>
      <div className='container'>1</div>
      <div className='container'>2</div>
      <div className='container'>3</div>
      <div className='container'>4</div>
    </div>
  );
}

export default Layout;
