import React, { useEffect, useRef } from 'react';
import '../App.css';

function Layout(props) {
    const wrapRef = useRef();

    useEffect(()=> {
        const wheelHandler = (e) => {
            e.preventDefault();
        }

        const wrapRefCurrent = wrapRef.current;
        wrapRefCurrent.addEventListner("wheel" , wheelHandler);

        return () => {
            wrapRefCurrent.removeEventListener('wheel',wheelHandler);
        }
    })
  return (
    <div className='full-page-scroll' ref={wrapRef}>
      <div className='container'>1</div>
      <div className='container'>2</div>
      <div className='container'>3</div>
      <div className='container'>4</div>
    </div>
  );
}

export default Layout;
