import React, { useState } from 'react';
import './App.css';

function Layout(props) {
  const [activePage, setActivePage] = useState(0);

  const handleScroll = (e) => {
    if (e.deltaY > 0) {
      setActivePage((prevPage) => (prevPage < 3 ? prevPage + 1 : prevPage));
    } else if (e.deltaY < 0) {
      setActivePage((prevPage) => (prevPage > 0 ? prevPage - 1 : prevPage));
    }
  };

  return (
    <div className='full-page-scroll' onWheel={handleScroll}>
      <div className={`container ${activePage === 0 ? 'active' : ''}`}>1</div>
      <div className={`container ${activePage === 1 ? 'active' : ''}`}>2</div>
      <div className={`container ${activePage === 2 ? 'active' : ''}`}>3</div>
      <div className={`container ${activePage === 3 ? 'active' : ''}`}>4</div>
    </div>
  );
}

export default Layout;
